CREATE OR REPLACE PROCEDURE copiar_param_conta_estab ( cd_estabelecimento_par_p bigint, cd_sequencia_parametro_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_empresa_w				integer;
ds_observacao_w				varchar(255);
nm_estab_origem_w			varchar(80);
dt_atualizacao_w				varchar(110);


BEGIN

cd_empresa_w		:= obter_empresa_estab(cd_estabelecimento_par_p);
dt_atualizacao_w		:= to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss');
nm_estab_origem_w	:= obter_nome_estabelecimento(cd_estabelecimento_par_p);
ds_observacao_w		:= WHEB_MENSAGEM_PCK.get_texto(299137, 'DT_DUPLIC=' || dt_atualizacao_w ||
							';NM_DUPLIC=' || nm_usuario_p ||
							';NM_ESTAB='||nm_estab_origem_w);

begin

insert into parametros_conta_contabil(
	cd_sequencia_parametro,
	cd_estabelecimento,
	cd_conta_receita,
	cd_conta_estoque,
	cd_conta_passag_direta,
	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	cd_material,
	nr_seq_familia, --10
	cd_area_proced,
	cd_especial_proced,
	cd_grupo_proced,
	cd_procedimento,
	nm_usuario,
	dt_atualizacao,
	ie_origem_proced,
	cd_conta_despesa,
	cd_setor_atendimento,
	ie_tipo_atendimento, --20
	ie_classif_convenio,
	cd_convenio,
	ie_clinica,
	cd_local_estoque,
	cd_operacao_estoque,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_centro_custo,
	dt_inicio_vigencia,
	dt_fim_vigencia, --30
	nr_seq_grupo,
	nr_seq_subgrupo,
	nr_seq_forma_org,
	ds_observacao,
	cd_categoria_convenio,
	ie_tipo_convenio,
	cd_plano,
	cd_empresa,
	nr_seq_motivo_solic,
	cd_sequencia_param_ref,
	nr_seq_proc_interno,
	nr_seq_regra_valor)
SELECT	nextval('parametros_conta_contabil_seq'),
	cd_estabelecimento_par_p,
	cd_conta_receita,
	cd_conta_estoque,
	cd_conta_passag_direta,
	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	cd_material,
	nr_seq_familia, --10
	cd_area_proced,
	cd_especial_proced,
	cd_grupo_proced,
	cd_procedimento,
	nm_usuario_p,
	clock_timestamp(),
	ie_origem_proced,
	cd_conta_despesa,
	cd_setor_atendimento,
	ie_tipo_atendimento, --20
	ie_classif_convenio,
	cd_convenio,
	ie_clinica,
	cd_local_estoque,
	cd_operacao_estoque,
	clock_timestamp(),
	nm_usuario_p,
	cd_centro_custo,
	dt_inicio_vigencia,
	dt_fim_vigencia, --30
	nr_seq_grupo,
	nr_seq_subgrupo,
	nr_seq_forma_org,
	substr(ds_observacao_w || chr(13) || chr(10) || ds_observacao,1,255),
	cd_categoria_convenio,
	ie_tipo_convenio,
	cd_plano,
	cd_empresa_w,
	nr_seq_motivo_solic,
	cd_sequencia_param_ref,
	nr_seq_proc_interno,
	nr_seq_regra_valor
from	parametros_conta_contabil
where	cd_sequencia_parametro	= cd_sequencia_parametro_p;

end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
