SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE copiar_pat_grupo_tipo ( cd_empresa_p bigint, cd_estabelecimento_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		bigint;
ds_grupo_w			varchar(255);
nr_seq_grupo_w			bigint;
nr_sequencia_w			bigint;

c01 CURSOR FOR
SELECT	cd_estabelecimento
from	estabelecimento
where	cd_empresa	= cd_empresa_p
and	cd_estabelecimento	<> cd_estabelecimento_p
and	ie_situacao	= 'A';

c02 CURSOR FOR
SELECT	nr_sequencia,
	ds_grupo
from	pat_grupo_tipo
where	cd_estabelecimento	= cd_estabelecimento_p
and	nr_sequencia	= coalesce(nr_sequencia_p,nr_sequencia);



BEGIN

open C01;
loop
fetch C01 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_grupo_w,
		ds_grupo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	nextval('pat_grupo_tipo_seq')
		into STRICT	nr_sequencia_w
		;

		insert into pat_grupo_tipo(
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			ds_grupo,
			ie_situacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_empresa)
		values (	nr_Sequencia_w,
			cd_estabelecimento_w,
			clock_timestamp(),
			nm_usuario_p,
			ds_grupo_w,
			'A',
			clock_timestamp(),
			nm_usuario_p,
			cd_empresa_p);

		insert into pat_tipo_bem(
			nr_sequencia,
			nr_seq_grupo,
			dt_atualizacao,
			nm_usuario,
			ds_tipo,
			ie_situacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		SELECT	nextval('pat_tipo_bem_seq'),
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			ds_tipo,
			'A',
			clock_timestamp(),
			nm_usuario_p
		from	pat_tipo_bem
		where	nr_seq_grupo	= nr_seq_grupo_w;
		end;
	end loop;
	close C02;


	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
