CREATE OR REPLACE PROCEDURE copiar_preco_tabela_conv ( cd_estabelecimento_p bigint, cd_tabela_origem_p bigint, cd_tabela_destino_p bigint, ie_copiar_p text, nm_usuario_p text) AS $body$
BEGIN
if (ie_copiar_p = 'S') then
	delete from preco_servico
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_tabela_servico	= cd_tabela_destino_p;
end if;
	insert into PRECO_SERVICO(CD_ESTABELECIMENTO,
		CD_TABELA_SERVICO,
		CD_PROCEDIMENTO,
		DT_INICIO_VIGENCIA,
		VL_SERVICO,
		CD_MOEDA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		IE_ORIGEM_PROCED,
		CD_UNIDADE_MEDIDA,
		DT_INATIVACAO,
		DT_VIGENCIA_FINAL)
	(SELECT cd_estabelecimento_p,
		cd_tabela_destino_p,
		cd_procedimento,
		dt_inicio_vigencia,
		vl_servico,
		cd_moeda,
		clock_timestamp(),
		nm_usuario_p,
		ie_origem_proced,
		cd_unidade_medida,
		DT_INATIVACAO,
		DT_VIGENCIA_FINAL
	 from	preco_servico
	 where	cd_estabelecimento	= cd_estabelecimento_p
	 and	cd_tabela_servico	= cd_tabela_origem_p);
commit;
end;
$body$
