CREATE OR REPLACE PROCEDURE copiar_preco_tab_mat ( cd_estabelecimento_p bigint, cd_tabela_origem_p bigint, cd_tabela_destino_p bigint, ie_copiar_p text, nm_usuario_p text) AS $body$
BEGIN
if (ie_copiar_p = 'S') then
	delete from preco_material
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_tab_preco_mat	= cd_tabela_destino_p;
end if;
	insert into preco_material(cd_estabelecimento,
		cd_tab_preco_mat,
		cd_material,
		dt_inicio_vigencia,
		vl_preco_venda,
		cd_moeda,
		ie_brasindice,
		dt_atualizacao,
		nm_usuario,
		cd_unidade_medida,
		ie_situacao,
		nr_sequencia_nf,
		nr_item_nf,
		cd_cgc_fornecedor)
	(SELECT cd_estabelecimento_p,
		cd_tabela_destino_p,
		cd_material,
		dt_inicio_vigencia,
		vl_preco_venda,
		cd_moeda,
		ie_brasindice,
		clock_timestamp(),
		nm_usuario_p,
		cd_unidade_medida,
		ie_situacao,
		nr_sequencia_nf,
		nr_item_nf,
		cd_cgc_fornecedor
	 from	preco_material
	 where	cd_estabelecimento	= cd_estabelecimento_p
	 and	cd_tab_preco_mat	= cd_tabela_origem_p);
commit;
end;
