CREATE OR REPLACE PROCEDURE copiar_profissional_escala ( nr_seq_ecala_orig_p bigint, nr_seq_ecala_dest_p bigint, nm_usuario_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
nr_seq_classif_escala_w		bigint;
ds_consistencia_w		varchar(255);
ds_consistencia_anestesita_w	varchar(255);

c01 CURSOR FOR					 
	SELECT	a.cd_pessoa_fisica, 
		nr_seq_classif_escala 
	from	escala_diaria_adic a, 
		escala_diaria b 
	where	b.nr_sequencia = a.nr_seq_escala_diaria 
	and	nr_seq_escala_diaria	=	nr_seq_ecala_orig_p 
	and	not exists (	SELECT	1 
				from	escala_diaria_adic x 
				where	x.nr_seq_escala_diaria = nr_seq_ecala_dest_p 
				and	x.cd_pessoa_fisica = a.cd_pessoa_fisica);

BEGIN
 
if	(nr_seq_ecala_orig_p > 0 AND nr_seq_ecala_dest_p > 0) then 
	ds_consistencia_w	:= null;
 
	open c01;
	loop 
		fetch c01 into	cd_pessoa_fisica_w, 
				nr_seq_classif_escala_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		 
		select	replace(obter_se_prof_liberado_escala(cd_pessoa_fisica_w,b.nr_seq_escala,b.dt_inicio), wheb_mensagem_pck.get_texto(306770, null),'') -- O anestesista 
		into STRICT	ds_consistencia_anestesita_w 
		from	escala_diaria b 
		where	b.nr_sequencia = 	nr_seq_ecala_dest_p;
		 
 
		if (ds_consistencia_anestesita_w IS NOT NULL AND ds_consistencia_anestesita_w::text <> '') then 
 
			if (length(ds_consistencia_w || ds_consistencia_anestesita_w || chr(10)) < 255) then 
				ds_consistencia_w	:= ds_consistencia_w || ds_consistencia_anestesita_w || chr(10);
				 
			end if;
				 
		else 
			insert into escala_diaria_adic(	nr_sequencia, 
							nr_seq_escala_diaria, 
							dt_atualizacao, 
							nm_usuario, 
							cd_pessoa_fisica, 
							dt_atualizacao_nrec, 
							nm_usuario_nrec, 
							nr_seq_classif_escala) 
						values (nextval('escala_diaria_adic_seq'), 
							nr_seq_ecala_dest_p, 
							clock_timestamp(), 
							nm_usuario_p, 
							cd_pessoa_fisica_w, 
							clock_timestamp(), 
							nm_usuario_p, 
							nr_seq_classif_escala_w);
		end if;
	end loop;
	close c01;
 
	commit;
end if;
 
if (ds_consistencia_w IS NOT NULL AND ds_consistencia_w::text <> '') then 
	 
	ds_consistencia_p := wheb_mensagem_pck.get_texto(306314, 'DS_CONSISTENCIA_W=' || ds_consistencia_w); -- ConsistÃªncias: #@DS_CONSISTENCIA_W#@ 
	
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
