CREATE OR REPLACE PROCEDURE copiar_protocolo_descricao (cd_protocolo_orig_p bigint, cd_protocolo_dest_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
