c01 CURSOR FOR
	SELECT	ds_frase,
		ie_padrao,
		qt_tamanho_fonte,
		ie_sexo,
		nr_seq_grupo,
		ds_cor_fonte,
		ds_estilo_fonte,
		cd_interno,
		nr_seq_apres,
		qt_recuo_texto,
		qt_espaco_linha,
                nr_sequencia
	from	Protocolo_Descricao
	where	cd_protocolo	= cd_protocolo_orig_p;



