	values (cd_protocolo_dest_p,
		nr_sequencia_w,
		ds_frase_w,
		ie_padrao_w,
		clock_timestamp(),
		nm_usuario_p,
		qt_tamanho_fonte_w,
		ie_sexo_w,
		nr_seq_grupo_w,
		ds_cor_fonte_w,
		ds_tipo_fonte_w,
		ds_estilo_fonte_w,
		cd_interno_w,
		nr_seq_apres_w,
		qt_recuo_texto_w,
