C02 CURSOR FOR
SELECT	nr_seq_exame
from	exame_laboratorio
where	nr_seq_exame <> nr_seq_exame_p;


BEGIN
if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	coalesce(max(cd_estabelecimento),0),
