						ie_regra_medico)
					values (	nextval('exame_lab_liberacao_seq'),
						nr_seq_exame_w,
						cd_estabelecimento_w,
						clock_timestamp(),
						nm_usuario_p,
						CASE WHEN cd_perfil_w=0 THEN null  ELSE cd_perfil_w END ,
						CASE WHEN nm_usuario_lib_w='X' THEN null  ELSE nm_usuario_lib_w END ,
						clock_timestamp(),
