CREATE OR REPLACE PROCEDURE copiar_resp_estrutura_mat ( nr_seq_resp_orig_p bigint, cd_material_p text, cd_grupo_material_p text, cd_subgrupo_material_p text, cd_classe_material_p text, nr_seq_familia_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_material_w		integer;
qt_registro_w		bigint;
nr_seq_localizacao_w	bigint;
cd_pessoa_fisica_w	varchar(10);
dt_inicio_vigencia_w	timestamp;
dt_fim_vigencia_w	timestamp;

c01 CURSOR FOR
	SELECT 	distinct m.cd_material
	from	material m,
		estrutura_material_v v
	where	m.cd_material = v.cd_material
	and	m.cd_material 			= coalesce(cd_material_p,m.cd_material)
	and	coalesce(m.cd_classe_material,0)	= coalesce(cd_classe_material_p,coalesce(m.cd_classe_material,0))
	and	coalesce(m.nr_seq_familia,0)		= coalesce(nr_seq_familia_p,coalesce(m.nr_seq_familia,0))
	and	coalesce(v.cd_grupo_material,0)	= coalesce(cd_grupo_material_p,coalesce(v.cd_grupo_material,0))
	and	coalesce(v.cd_subgrupo_material,0)	= coalesce(cd_subgrupo_material_p,coalesce(v.cd_subgrupo_material,0));


BEGIN

select	cd_pessoa_fisica,
	nr_seq_localizacao,
	dt_inicio_vigencia,
	dt_fim_vigencia
into STRICT	cd_pessoa_fisica_w,
	nr_seq_localizacao_w,
	dt_inicio_vigencia_w,
	dt_fim_vigencia_w
from	material_resp_matricial
where	nr_sequencia = nr_seq_resp_orig_p;

open c01;
loop
fetch c01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	count(*)
	into STRICT	qt_registro_w
	from	material_resp_matricial
	where	cd_material = cd_material_w;

	if (qt_registro_w = 0) then
		insert into material_resp_matricial(
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			nr_seq_localizacao,
			dt_inicio_vigencia,
			dt_fim_vigencia,
			cd_material)
		values (	nextval('material_resp_matricial_seq'),
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_w,
			nr_seq_localizacao_w,
			dt_inicio_vigencia_w,
			dt_fim_vigencia_w,
			cd_material_w);
	end if;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
