CREATE OR REPLACE PROCEDURE copiar_setores_proc_interno ( nr_proc_int_origem_p bigint, cd_proc_dest_p bigint, ie_origem_dest_p bigint, ds_proc_interno_dest_p text, nm_usuario_p text) AS $body$
DECLARE


cd_proc_faturamento_w		bigint;
ds_proc_interno_w		varchar(4000);
nr_seq_proc_interno_w		bigint;
qt_registro_w			bigint:=0;
ie_origem_dest_w		integer;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	proc_interno
	where	((obter_se_contido(nr_sequencia,elimina_aspas(ds_proc_interno_w)) = 'S') or (ds_proc_interno_w = '0'))
	and 	((cd_proc_faturamento_w = cd_procedimento) or (cd_proc_faturamento_w = 0))
	and	((ie_origem_dest_w = ie_origem_proced) or (ie_origem_dest_w = 0))
	and	nr_proc_int_origem_p <> nr_sequencia;


BEGIN

cd_proc_faturamento_w	:= coalesce(cd_proc_dest_p,0);
ie_origem_dest_w	:= coalesce(ie_origem_dest_p,0);
ds_proc_interno_w	:= coalesce(replace(replace(replace(ds_proc_interno_dest_p,obter_desc_expressao(347248),''),chr(10),''),chr(13),''),'0');


open C01;
loop
fetch C01 into
	nr_seq_proc_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select 	count(*)
	into STRICT	qt_registro_w
	from	proc_interno_setor
	where	nr_seq_proc_interno = nr_proc_int_origem_p;

	if (qt_registro_w > 0) then

	insert into proc_interno_setor(	cd_estabelecimento,
					cd_setor_atendimento,
					cd_setor_origem,
					dt_atualizacao,
					dt_atualizacao_nrec,
					ie_banco_sangue_rep,
					ie_tipo_atendimento,
					nm_usuario,
					nm_usuario_nrec,
					nr_prioridade,
					nr_seq_proc_interno,
					nr_sequencia,
					ie_tipo_convenio,
					nr_seq_agrupamento,
					cd_especialidade)
				SELECT 	cd_estabelecimento,
					cd_setor_atendimento,
					cd_setor_origem,
					clock_timestamp(),
					clock_timestamp(),
					ie_banco_sangue_rep,
					ie_tipo_atendimento,
					nm_usuario_p,
					nm_usuario_p,
					nr_prioridade,
					nr_seq_proc_interno_w,
					nextval('proc_interno_setor_seq'),
					ie_tipo_convenio,
					nr_seq_agrupamento,
					cd_especialidade
				from	proc_interno_setor
				where	nr_seq_proc_interno = nr_proc_int_origem_p;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
