CREATE OR REPLACE PROCEDURE copiar_tabela_outro_estab ( cd_tabela_servico_p bigint, cd_estabelecimento_p bigint, cd_procedimento_p bigint, dt_inicio_vigencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;
cd_estabelecimento_w	smallint;

C01 CURSOR FOR
	SELECT	cd_estabelecimento
	from	estabelecimento
	where	cd_estabelecimento <> cd_estabelecimento_p
	and	ie_situacao = 'A';


BEGIN

open C01;
loop
fetch C01 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	tabela_servico
	where	cd_tabela_servico = cd_tabela_servico_p
	and	cd_estabelecimento = cd_estabelecimento_w;

	if (qt_existe_w > 0) then

		insert into preco_servico(
			cd_estabelecimento,
			cd_tabela_servico,
			cd_procedimento,
			dt_inicio_vigencia,
			vl_servico,
			cd_moeda,
			dt_atualizacao,
			nm_usuario,
			ie_origem_proced,
			cd_unidade_medida,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_inativacao,
			dt_vigencia_final)
		SELECT	cd_estabelecimento_w,
			cd_tabela_servico,
			cd_procedimento,
			dt_inicio_vigencia,
			vl_servico,
			cd_moeda,
			clock_timestamp(),
			nm_usuario_p,
			ie_origem_proced,
			cd_unidade_medida,
			clock_timestamp(),
			nm_usuario_p,
			dt_inativacao,
			dt_vigencia_final
		from	preco_servico
		where	cd_tabela_servico = cd_tabela_servico_p
		and	cd_estabelecimento = cd_estabelecimento_p
		and	cd_procedimento	= cd_procedimento_p
		and	dt_inicio_vigencia = dt_inicio_vigencia_p;

	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
