CREATE OR REPLACE PROCEDURE copiar_tabela_preco_mat_estab (cd_tab_preco_mat_p bigint, cd_estabelecimento_p bigint, cd_material_p bigint, dt_inicio_vigencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w				bigint;
cd_estabelecimento_w	smallint;
ie_existe_tab_w			varchar(1);
vl_preco_venda_w		preco_material.vl_preco_venda%type;

C01 CURSOR FOR
	SELECT	cd_estabelecimento
	from	estabelecimento
	where	cd_estabelecimento <> cd_estabelecimento_p
	and	ie_situacao = 'A';


BEGIN

open C01;
loop
fetch C01 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	preco_material
	where	cd_tab_preco_mat = cd_tab_preco_mat_p
	and	cd_estabelecimento = cd_estabelecimento_w;

	select	coalesce(max('S'),'N')
	into STRICT	ie_existe_tab_w
	from	preco_material
	where	cd_tab_preco_mat = cd_tab_preco_mat_p
	and	cd_estabelecimento = cd_estabelecimento_w
	and	cd_material	= cd_material_p
	and	dt_inicio_vigencia = dt_inicio_vigencia_p;

	if (qt_existe_w > 0) and (ie_existe_tab_w = 'N') then
		insert into preco_material(
			cd_estabelecimento,
			cd_tab_preco_mat,
			cd_material,
			dt_inicio_vigencia,
			vl_preco_venda,
			cd_moeda,
			ie_brasindice,
			dt_atualizacao,
			nm_usuario,
			ie_situacao,
			ie_preco_venda,
			ie_integracao,
			cd_cgc_fornecedor)
		SELECT	cd_estabelecimento_w,
			cd_tab_preco_mat,
			cd_material,
			dt_inicio_vigencia,
			vl_preco_venda,
			cd_moeda,
			ie_brasindice,
			dt_atualizacao,
			nm_usuario,
			ie_situacao,
			ie_preco_venda,
			ie_integracao,
			cd_cgc_fornecedor
		from	preco_material
		where	cd_tab_preco_mat = cd_tab_preco_mat_p
		and	cd_estabelecimento = cd_estabelecimento_p
		and	cd_material	= cd_material_p
		and	dt_inicio_vigencia = dt_inicio_vigencia_p;

	elsif (ie_existe_tab_w = 'S') then

		select	max(vl_preco_venda)
		into STRICT	vl_preco_venda_w
		from	preco_material
		where	cd_tab_preco_mat = cd_tab_preco_mat_p
		and	cd_estabelecimento = cd_estabelecimento_p
		and	cd_material	= cd_material_p
		and	dt_inicio_vigencia = dt_inicio_vigencia_p;

		update	preco_material
		set	vl_preco_venda = vl_preco_venda_w
		where	cd_tab_preco_mat = cd_tab_preco_mat_p
		and	cd_estabelecimento = cd_estabelecimento_w
		and	cd_material	= cd_material_p
		and	dt_inicio_vigencia = dt_inicio_vigencia_p;

	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
