SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE copiar_tab_fornec_mult_conv ( nr_sequencia_p bigint, cd_list_conv_p text, ie_tipo_acao_p text, nm_usuario_p text ) AS $body$
DECLARE


cd_convenio_w     convenio.cd_convenio%type;
cd_cgc_fornec_w   convenio_preco_fornec.cd_cgc_fornec%type;
cd_estab_w        convenio_preco_fornec.cd_estabelecimento%type;
nr_sequencia_w    convenio_preco_fornec.nr_sequencia%type;
nr_count_w        bigint;

c01 CURSOR FOR
  SELECT a.cd_convenio
  from   convenio a
  where (coalesce(ie_tipo_acao_p,'1') = '1' and (obter_se_contido(a.cd_convenio, elimina_aspas(cd_list_conv_p)) = 'S'))
         or (coalesce(ie_tipo_acao_p,'2') = '2' and (obter_se_contido(a.cd_convenio, elimina_aspas(cd_list_conv_p)) = 'N'));


BEGIN

  select a.cd_cgc_fornec,
         a.cd_estabelecimento
  into STRICT   cd_cgc_fornec_w,
         cd_estab_w
  from   convenio_preco_fornec a
  where  a.NR_SEQUENCIA  = NR_SEQUENCIA_P;

  open c01;
  loop fetch c01
    into cd_convenio_w;
  EXIT WHEN NOT FOUND; /* apply on c01 */

    select nextval('convenio_preco_fornec_seq')
    into STRICT   nr_sequencia_w
;

    insert into convenio_preco_fornec(
      nr_sequencia,
      cd_estabelecimento,
      dt_atualizacao,
      nm_usuario,
      dt_atualizacao_nrec,
      nm_usuario_nrec,
      cd_convenio,
      cd_cgc_fornec,
      dt_inicio_vigencia,
      dt_fim_vigencia )
    SELECT	nr_sequencia_w,
      cd_estabelecimento,
      clock_timestamp(),
      nm_usuario_p,
      clock_timestamp(),
      nm_usuario_p,
      cd_convenio_w,
      cd_cgc_fornec,
      dt_inicio_vigencia,
      dt_fim_vigencia
    from	convenio_preco_fornec
    where	nr_sequencia 		    = nr_sequencia_p
    and	  cd_estabelecimento 	= cd_estab_w;

    insert into convenio_preco_mat_fornec(
      nr_sequencia,
      dt_atualizacao,
      nm_usuario,
      dt_atualizacao_nrec,
      nm_usuario_nrec,
      nr_seq_conv_preco,
      cd_material,
      vl_material )
    SELECT	nextval('convenio_preco_mat_fornec_seq'),
      clock_timestamp(),
      nm_usuario_p,
      clock_timestamp(),
      nm_usuario_p,
      nr_sequencia_w,
      cd_material,
      vl_material
    from	convenio_preco_mat_fornec
    where	nr_seq_conv_preco	= nr_sequencia_p;

  end loop;
  close c01;

  commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
