SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE copiar_transf_agenda_tasy ( nr_seq_origem_p bigint, nr_seq_destino_p bigint, ie_copiar_transferir_p text, nm_usuario_p text) AS $body$
DECLARE


ie_status_w		varchar(1);
nr_minuto_duracao_w	bigint;
dt_final_w		timestamp;
nr_seq_tipo_w		bigint;
ds_agenda_w		varchar(100);
ds_local_w		varchar(50);
nr_seq_local_w		bigint;
ds_observacao_w		varchar(2000);
nr_sequencia_w		bigint;
nm_usuario_convidado_w	varchar(15);
ie_confirmado_w		varchar(1);
ds_motivo_ausencia_w	varchar(255);
dt_confirmacao_w		timestamp;
cd_pessoa_convidado_w	varchar(10);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		nm_usuario_convidado,
		ie_confirmado,
		ds_motivo_ausencia,
		dt_confirmacao,
		cd_pessoa_convidado
	from	agenda_tasy_convite
	where	nr_seq_agenda	= nr_seq_origem_p;



BEGIN
if (nr_seq_origem_p IS NOT NULL AND nr_seq_origem_p::text <> '') and (nr_seq_destino_p IS NOT NULL AND nr_seq_destino_p::text <> '') and (ie_copiar_transferir_p IS NOT NULL AND ie_copiar_transferir_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	ie_status,
		nr_minuto_duracao,
		dt_final,
		nr_seq_tipo,
		ds_agenda,
		ds_local,
		nr_seq_local,
		ds_observacao
	into STRICT	ie_status_w,
		nr_minuto_duracao_w,
		dt_final_w,
		nr_seq_tipo_w,
		ds_agenda_w,
		ds_local_w,
		nr_seq_local_w,
		ds_observacao_w
	from	agenda_tasy
	where	nr_sequencia = nr_seq_origem_p;

	update	agenda_tasy
	set	ie_status		= ie_status_w,
		nr_minuto_duracao	= nr_minuto_duracao_w,
		dt_final		= dt_final_w,
		nr_seq_tipo	= nr_seq_tipo_w,
		ds_agenda	= ds_agenda_w,
		ds_local		= ds_local_w,
		nr_seq_local	= nr_seq_local_w,
		ds_observacao	= ds_observacao_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_destino_p;

	open c01;
	loop
	fetch c01 into
		nr_sequencia_w,
		nm_usuario_convidado_w,
		ie_confirmado_w,
		ds_motivo_ausencia_w,
		dt_confirmacao_w,
		cd_pessoa_convidado_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ie_copiar_transferir_p = 'T') then
			update	agenda_tasy_convite
			set	nr_seq_agenda		= nr_seq_destino_p,
				nm_usuario_convidado	= nm_usuario_convidado_w
			where	nr_sequencia		= nr_sequencia_w;
		elsif (ie_copiar_transferir_p = 'C') then
			insert	into agenda_tasy_convite(
				nr_seq_agenda,
				nm_usuario_convidado,
				dt_atualizacao,
				nm_usuario,
				ie_confirmado,
				ds_motivo_ausencia,
				dt_confirmacao,
				nr_sequencia,
				cd_pessoa_convidado)
			values (nr_seq_destino_p,
				nm_usuario_convidado_w,
				clock_timestamp(),
				nm_usuario_p,
				ie_confirmado_w,
				ds_motivo_ausencia_w,
				dt_confirmacao_w,
				nextval('agenda_tasy_convite_seq'),
				cd_pessoa_convidado_w);
		end if;
		end;
	end loop;
	close c01;

	if (ie_copiar_transferir_p = 'T') then
		begin
		CALL gerar_comunic_transf_agetasy(nr_seq_destino_p, nm_usuario_p);
		delete
		from	agenda_tasy
		where	nr_sequencia = nr_seq_origem_p;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
