CREATE OR REPLACE PROCEDURE copiar_ultima_avaliacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_tipo_avaliacao_w	bigint;
cd_pessoa_fisica_w	varchar(10);


BEGIN 
 
select	max(nr_seq_tipo_avaliacao), 
	max(cd_pessoa_fisica) 
into STRICT	nr_seq_tipo_avaliacao_w, 
	cd_pessoa_fisica_w 
from	med_avaliacao_paciente 
where	nr_sequencia = nr_sequencia_p;
 
insert into	Med_Avaliacao_Result( 
	nr_seq_avaliacao, 
	nr_seq_item, 
	dt_atualizacao, 
	nm_usuario, 
	qt_resultado, 
	ds_resultado) 
SELECT	nr_sequencia_p, 
	nr_seq_item, 
	clock_timestamp(), 
	nm_usuario_p, 
	qt_resultado, 
	ds_resultado 
from	Med_Avaliacao_Result a 
where	nr_seq_avaliacao =	(	SELECT	max(nr_sequencia) 
					from	med_avaliacao_paciente 
					where	nr_seq_tipo_avaliacao	= nr_seq_tipo_avaliacao_w 
					and	cd_pessoa_fisica	= cd_pessoa_fisica_w 
					and	nr_sequencia		<> nr_sequencia_p) 
and	not exists (	select	1 
					from	Med_Avaliacao_Result x 
					where	x.nr_seq_avaliacao = nr_sequencia_p 
					and		x.nr_seq_item		= a.nr_seq_item);	
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
