CREATE OR REPLACE PROCEDURE copiar_visao ( nr_seq_origem_p bigint, nr_seq_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	tabela_visao_atributo
where	nr_sequencia = nr_seq_destino_p;

if (qt_registro_w = 0) then

	insert into tabela_visao_atributo(
