		vl_padrao,
		ds_mascara,
		ie_readonly,
		ie_tabstop,
		qt_altura,
		cd_dominio,
		nr_seq_localizador,
		ds_valores,
		nr_seq_ordem,
		qt_coluna,
		ie_criar_descricao,
		ie_obrigatorio,
		ie_componente,
		nm_atributo_pai,
		ie_tipo_botao,
		cd_exp_desc,
		cd_exp_label_grid,
		cd_exp_valores,
		cd_exp_label,
		cd_exp_label_longo,
		nr_linha,
		nr_seq_visao_grupo,
		ie_unit_col_width,
		cd_mascara,
		nr_seq_dic_objeto,
		qt_tam_html,
		ie_ancorar_atrib_esq,
		ie_nova_linha,
		nr_seq_visao_subgrupo,
		ie_dock_right)
	SELECT	nr_seq_destino_p,
		nm_atributo,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apresent,
		qt_tam_delphi,
		qt_tam_grid,
		qt_desloc_direita,
		nr_seq_grid,
		nr_seq_tabstop,
		ds_label,
		ds_label_grid,
		vl_padrao,
		ds_mascara,
		ie_readonly,
		ie_tabstop,
		qt_altura,
		cd_dominio,
		nr_seq_localizador,
		ds_valores,
		nr_seq_ordem,
		qt_coluna,
		ie_criar_descricao,
		ie_obrigatorio,
		ie_componente,
		nm_atributo_pai,
		ie_tipo_botao,
		cd_exp_desc,
		cd_exp_label_grid,
		cd_exp_valores,
		cd_exp_label,
		cd_exp_label_longo,
		nr_linha,
		nr_seq_visao_grupo,
		ie_unit_col_width,
