CREATE OR REPLACE PROCEDURE copia_especialidades_classif ( nr_sequencias_p text, nr_seq_classificacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_inicio_vigencia_w	timestamp;
dt_fim_vigencia_w		timestamp;
cd_especialidade_w	pessoa_classif_espec.cd_especialidade%type;

C01 CURSOR FOR
	SELECT	dt_inicio_vigencia,
			dt_fim_vigencia,
			cd_especialidade
	FROM	pessoa_classif_espec
	WHERE	obter_se_contido(nr_sequencia,nr_sequencias_p) = 'S'
	ORDER BY dt_inicio_vigencia;

BEGIN

OPEN C01;
LOOP
FETCH C01 INTO
	dt_inicio_vigencia_w,
	dt_fim_vigencia_w,
	cd_especialidade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	BEGIN
	INSERT INTO pessoa_classif_espec(
		nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
