		nm_usuario,
		cd_especialidade,
		dt_inicio_vigencia,
		dt_fim_vigencia,
		nr_seq_classif_pf)
	VALUES (nextval('pessoa_classif_espec_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_especialidade_w,
		dt_inicio_vigencia_w,
		dt_fim_vigencia_w,
		nr_seq_classificacao_p);
	END;
