CREATE OR REPLACE PROCEDURE copia_meu_texto_padrao ( cd_medico_origem_p text, cd_medico_destino_p text, nm_usuario_p text, nr_sequencia_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN

select	nextval('med_texto_padrao_seq')
into STRICT	nr_sequencia_w
;

Insert into med_texto_padrao(
		NR_SEQUENCIA,
        CD_MEDICO,
		DS_TEXTO,
		DS_TITULO,
		DT_ATUALIZACAO,
		NM_USUARIO,
		IE_TIPO_TEXTO,
