		DS_DADOS_CLINICOS,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		NR_SEQ_ITEM_PRONT,
		IE_OPCAO_PADRAO,
		NR_SEQ_ITEM_OFT,
		NR_SEQ_GRUPO,
		IE_EVOLUCAO_CLINICA)
SELECT	nr_sequencia_w,
        cd_medico_destino_p,
		'',
		DS_TITULO,
		clock_timestamp(),
		nm_usuario_p,
		IE_TIPO_TEXTO,
		NR_SEQ_EXAME,
		DS_DADOS_CLINICOS,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		NR_SEQ_ITEM_PRONT,
		IE_OPCAO_PADRAO,
		NR_SEQ_ITEM_OFT,
		NR_SEQ_GRUPO,
		IE_EVOLUCAO_CLINICA
FROM	med_texto_padrao
WHERE nr_sequencia = nr_sequencia_p;



CALL COPIA_CAMPO_LONG_DE_PARA('med_texto_padrao','ds_texto',' where nr_sequencia = :nr_sequencia ','nr_sequencia='||nr_sequencia_p||';',
						 'med_texto_padrao','ds_texto',' where nr_sequencia = :nr_sequencia','nr_sequencia='||nr_sequencia_w||';');

commit;

END;
