CREATE OR REPLACE PROCEDURE copia_padrao_estoque_local ( cd_local_estoque_p bigint, cd_local_estoque_dest_p bigint, ie_copia_localizacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;
cd_material_w			integer;
qt_estoque_maximo_w		double precision;
qt_estoque_minimo_w		double precision;
cd_local_atende_w			smallint;
ie_situacao_w			varchar(1);
nr_seq_contagem_w		integer;
ds_localizacao_w		padrao_estoque_local.ds_localizacao%type;
ie_atualiza_padrao_local_w		varchar(1);
nr_seq_estrut_mat_w		bigint;

nr_sequencia_w			bigint;
qt_estoque_max_regra_w		bigint;
qt_estoque_min_regra_w		bigint;
ie_dia_semana_w			varchar(15);

qt_dia_consumo_w			integer;
qt_dia_ajuste_minimo_w		integer;
qt_dia_ajuste_maximo_w		integer;
ie_atualiza_padrao_regra_w	padrao_estoque_local_regra.ie_atualiza_padrao_local%type;

c01 CURSOR FOR
SELECT	cd_material,
	qt_estoque_maximo,
	qt_estoque_minimo,
	cd_local_atende,
	ie_situacao,
	nr_seq_contagem,
	ds_localizacao,
	ie_atualiza_padrao_local,
	nr_seq_estrut_mat,
	qt_dia_consumo,
	qt_dia_ajuste_minimo,
	qt_dia_ajuste_maximo
from	padrao_estoque_local a
where	cd_estabelecimento = cd_estabelecimento_p
and	cd_local_estoque = cd_local_estoque_p
and not exists (select	1
		from	padrao_estoque_local x
		where	x.cd_local_estoque = cd_local_estoque_dest_p
		and	x.cd_material = a.cd_material);

c02 CURSOR FOR
SELECT	qt_estoque_maximo,
	qt_estoque_minimo,
	ie_dia_semana,
	ie_atualiza_padrao_local
from	padrao_estoque_local_regra
where	cd_local_estoque = cd_local_estoque_p
and	cd_material = cd_material_w;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	padrao_estoque_local
where	cd_estabelecimento = cd_estabelecimento_p
and	cd_local_estoque = cd_local_estoque_p;

if (qt_existe_w > 0) then
	begin

	open c01;
	loop
	fetch c01 into
		cd_material_w,
		qt_estoque_maximo_w,
		qt_estoque_minimo_w,
		cd_local_atende_w,
		ie_situacao_w,
		nr_seq_contagem_w,
		ds_localizacao_w,
		ie_atualiza_padrao_local_w,
		nr_seq_estrut_mat_w,
		qt_dia_consumo_w,
		qt_dia_ajuste_minimo_w,
		qt_dia_ajuste_maximo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		insert into padrao_estoque_local(
			cd_local_estoque,
			cd_material,
			dt_atualizacao,
			nm_usuario,
			qt_estoque_maximo,
			qt_estoque_minimo,
			cd_local_atende,
			cd_estabelecimento,
			ie_situacao,
			nr_seq_contagem,
			ds_localizacao,
			ie_atualiza_padrao_local,
			nr_seq_estrut_mat,
			qt_dia_consumo,
			qt_dia_ajuste_minimo,
			qt_dia_ajuste_maximo)
		values ( cd_local_estoque_dest_p,
			cd_material_w,
			clock_timestamp(),
			nm_usuario_p,
			qt_estoque_maximo_w,
			qt_estoque_minimo_w,
			cd_local_atende_w,
			cd_estabelecimento_p,
			ie_situacao_w,
			nr_seq_contagem_w,
			CASE WHEN coalesce(ie_copia_localizacao_p,'S')='S' THEN ds_localizacao_w  ELSE '' END ,
			ie_atualiza_padrao_local_w,
			nr_seq_estrut_mat_w,
			qt_dia_consumo_w,
			qt_dia_ajuste_minimo_w,
			qt_dia_ajuste_maximo_w);

		open c02;
		loop
		fetch c02 into
			qt_estoque_max_regra_w,
			qt_estoque_min_regra_w,
			ie_dia_semana_w,
			ie_atualiza_padrao_regra_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin

			select	nextval('padrao_estoque_local_regra_seq')
			into STRICT	nr_sequencia_w
			;

			insert into padrao_estoque_local_regra(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_local_estoque,
				cd_material,
				qt_estoque_maximo,
				qt_estoque_minimo,
				ie_dia_semana,
				ie_atualiza_padrao_local)
			values ( nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_local_estoque_dest_p,
				cd_material_w,
				qt_estoque_max_regra_w,
				qt_estoque_min_regra_w,
				ie_dia_semana_w,
				ie_atualiza_padrao_regra_w);

			end;
		end loop;
		close c02;

		end;
	end loop;
	close c01;

	commit;

	end;
end if;

end;
$body$
