SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE copia_parametro_convenio_regra ( nr_sequencia_p bigint, cd_convenio_orig_p bigint, cd_convenio_dest_p text, ie_tipo_acao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

				
				
cd_convenio_dest_w	integer;
nr_seq_w			bigint;

C01 CURSOR FOR
	SELECT	coalesce(cd_convenio,0)
	from	convenio
	where	(((ie_tipo_acao_p = '1') and (obter_se_contido(cd_convenio, elimina_aspas(cd_convenio_dest_p)) = 'S')) or
		((ie_tipo_acao_p = '2') and (not obter_se_contido(cd_convenio, elimina_aspas(cd_convenio_dest_p)) = 'S')) or (ie_tipo_acao_p = '3'));
		

BEGIN

open C01;
loop
fetch C01 into	
	cd_convenio_dest_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (cd_convenio_dest_w > 0) then
	
		select	nextval('regra_ajuste_material_seq')
		into STRICT	nr_seq_w
		;

		insert	into regra_ajuste_material(
				nr_sequencia,
				dt_inicio_vigencia, 
				ie_situacao,
				cd_grupo_material,
				cd_subgrupo_material,
				cd_classe_material,
				cd_material,
				tx_ajuste,
				vl_negociado,
				ie_preco_informado,
				ie_glosa,
				tx_brasindice_pfb,
				tx_brasindice_pmc,
				tx_afaturar,
				dt_final_vigencia,
				cd_tipo_acomodacao,
				ie_tipo_atendimento,
				cd_setor_atendimento,
				qt_idade_min,
				qt_idade_max,
				tx_simpro_pfb,
				tx_simpro_pmc,
				ie_origem_preco,
				ie_precedencia_preco,
				pr_glosa,
				ie_tipo_material,
				tx_pmc_neg,
				tx_pmc_pos,
				cd_cid,
				cd_tabela_preco,
				cd_motivo_exc_conta,
				cd_estabelecimento,
				cd_convenio,
				dt_atualizacao,
				nm_usuario, 
				ie_sexo, 
				IE_AUTOR_PARTICULAR, 
				ie_kit_material, 
				cd_kit_material,
				tx_pfb_neg,
				tx_pfb_pos,
				nr_seq_estrutura,
				ie_clinica,
				cd_convenio_glosa,
				cd_categoria_glosa,
				nr_seq_familia,
				ie_tipo_preco_autor)
		SELECT 	nr_seq_w, 
				dt_inicio_vigencia,  
				ie_situacao, 
				cd_grupo_material, 
				cd_subgrupo_material, 
				cd_classe_material, 
				cd_material,
				tx_ajuste, 
				vl_negociado, 
				ie_preco_informado, 
				ie_glosa,
				tx_brasindice_pfb,
				tx_brasindice_pmc, 
				tx_afaturar, 
				dt_final_vigencia,
				cd_tipo_acomodacao, 
				ie_tipo_atendimento, 
				cd_setor_atendimento, 
				qt_idade_min,
				qt_idade_max,
				tx_simpro_pfb, 
				tx_simpro_pmc, 
				ie_origem_preco,
				ie_precedencia_preco, 
				pr_glosa, 
				ie_tipo_material, 
				tx_pmc_neg,
				tx_pmc_pos, 
				cd_cid, 
				cd_tabela_preco, 
				cd_motivo_exc_conta, 
				cd_estabelecimento_p,
				cd_convenio_dest_w,
				clock_timestamp(),
				nm_usuario_p,
				ie_sexo, 
				ie_autor_particular, 
				ie_kit_material, 
				cd_kit_material,
				tx_pfb_neg,
				tx_pfb_pos,
				nr_seq_estrutura,
				ie_clinica,
				cd_convenio_glosa,
				cd_categoria_glosa,
				nr_seq_familia,
				ie_tipo_preco_autor
		from	regra_ajuste_material
		where	nr_sequencia = nr_sequencia_p;
		
		insert into regra_ajuste_mat_aviso(
				nr_sequencia,
				nr_seq_regra,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nm_usuario_aviso,
				cd_perfil_aviso)
		SELECT	nextval('regra_ajuste_mat_aviso_seq'),
				nr_seq_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_aviso,
				cd_perfil_aviso
		from	regra_ajuste_mat_aviso
		where	nr_seq_regra = nr_sequencia_p;
		
		insert into regra_ajuste_indice_dif(
				nr_sequencia,
				nr_seq_regra,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				vl_inicial,
				vl_final,
				tx_ajuste,
				ie_origem_preco)
		SELECT	nextval('regra_ajuste_indice_dif_seq'),
				nr_seq_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				vl_inicial,
				vl_final,
				tx_ajuste,
				ie_origem_preco
		from	regra_ajuste_indice_dif
		where	nr_seq_regra = nr_sequencia_p;

	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE copia_parametro_convenio_regra ( nr_sequencia_p bigint, cd_convenio_orig_p bigint, cd_convenio_dest_p text, ie_tipo_acao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

