CREATE OR REPLACE PROCEDURE copia_usu_lib_hist_ant (nm_usuario_p text, nr_seq_historico_p bigint, nr_sequencia_p bigint, nm_usuario_reg_p text) AS $body$
DECLARE


nm_usuario_lib_w      varchar(15);
nr_sequencia_w	       bigint;

c01 CURSOR FOR


SELECT  a.nm_usuario_lib
FROM 	cml_prospect_hist_lib a,
	cml_prospect_hist b,
	cml_prospect c
WHERE 	a.nr_seq_historico = b.nr_sequencia
AND	b.nr_seq_prospect = c.nr_sequencia
AND	b.nr_sequencia = (SELECT   MAX(nr_sequencia)
			   FROM     CML_PROSPECT_HIST
		           WHERE    nr_sequencia <> (SELECT MAX(nr_sequencia)
						     FROM CML_PROSPECT_HIST
						     WHERE nr_seq_prospect = nr_sequencia_p)
			 AND	 nr_seq_prospect = nr_Sequencia_p
			 AND	NM_USUARIO = nm_usuario_reg_p);


BEGIN


OPEN c01;
LOOP
FETCH c01 INTO
	nm_usuario_lib_w;

EXIT WHEN NOT FOUND; /* apply on c01 */
	BEGIN

	SELECT	nextval('cml_prospect_hist_lib_seq')
	INTO STRICT	nr_sequencia_w
	;


	INSERT INTO cml_prospect_hist_lib(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nm_usuario_lib,
				dt_leitura,
				nr_seq_historico
				)
				VALUES (
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_P,
				nm_usuario_lib_w,
				NULL,
				nr_seq_historico_p
				);
	END;
END LOOP;
CLOSE c01;


COMMIT;

END;
$body$
LANGUAGE PLPGSQL
