CREATE OR REPLACE PROCEDURE cotacao_atualiza_tab_opme ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_moeda_p bigint, dt_vigencia_p timestamp, vl_preco_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_tab_preco_mat_w	smallint;					
qt_existe_w		bigint;
				
c01 CURSOR FOR
SELECT	distinct
	cd_tab_preco_mat
from	convenio_preco_mat
where	cd_estabelecimento = cd_estabelecimento_p
and	ie_situacao = 'A'				  
and	ie_integracao_opme = 'S';
				

BEGIN

if (coalesce(vl_preco_p,0) > 0) then

	open C01;
	loop
	fetch C01 into	
		cd_tab_preco_mat_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		select	count(*)
		into STRICT	qt_existe_w
		from	preco_material
		where	cd_estabelecimento		= cd_estabelecimento_p
		and	cd_tab_preco_mat		= cd_tab_preco_mat_w
		and	cd_material		= cd_material_p
		and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicio_vigencia)	= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_vigencia_p);
		
		if (qt_existe_w = 0) then
		
			insert into preco_material(
				cd_estabelecimento,
				cd_tab_preco_mat,
				cd_material,
				dt_inicio_vigencia,
				vl_preco_venda,
				cd_moeda,
				ie_brasindice,
				dt_atualizacao,
				nm_usuario,
				ie_situacao,
				ie_preco_venda,
				ie_inpart)
			values (	cd_estabelecimento_p,
				cd_tab_preco_mat_w,
				cd_material_p,
				ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_vigencia_p),
				coalesce(vl_preco_p,0),
				cd_moeda_p,
				'N',
				clock_timestamp(),
				nm_usuario_p,
				'A',
				'S',
				'N');
			
		else			
			update 	preco_material
			set	vl_preco_venda 		= coalesce(vl_preco_p,0)
			where	cd_estabelecimento		= cd_estabelecimento_p
			and	cd_tab_preco_mat		= cd_tab_preco_mat_w
			and	cd_material		= cd_material_p
			and	dt_inicio_vigencia     	 = ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_vigencia_p);
		
		end if;	
		end;
	end loop;
	close C01;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
