SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cot_comunicar_historico ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_titulo_w			varchar(50);
ds_comunicado_w		varchar(4000);
nr_cot_compra_w		bigint;
nr_sequencia_w		bigint;
nr_seq_classif_w		bigint;
nm_usuario_w			varchar(15);


BEGIN

select	nr_cot_compra,
	ds_historico
into STRICT	nr_cot_compra_w,
	ds_comunicado_w
from	cot_compra_hist
where	nr_sequencia	= nr_sequencia_p;

ds_titulo_w			:= WHEB_MENSAGEM_PCK.get_texto(298943,'NR_COT_COMPRA_W='||nr_cot_compra_w);
select	obter_classif_comunic('F')
into STRICT	nr_seq_classif_w
;

select	min(b.nm_usuario)
into STRICT	nm_usuario_w
from	usuario b,
	cot_compra a
where	a.nr_cot_compra	= nr_cot_compra_w
and	a.cd_pessoa_solicitante	= b.cd_pessoa_fisica;

if (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then
	select nextval('comunic_interna_seq')
	into STRICT nr_sequencia_w
	;
	insert into comunic_interna(
		dt_comunicado, ds_titulo, ds_comunicado, nm_usuario,
		dt_atualizacao, ie_geral, nm_usuario_destino, nr_sequencia,
		ie_gerencial, nr_seq_classif, dt_liberacao)
	values (
		clock_timestamp(), ds_titulo_w, ds_comunicado_w, nm_usuario_p,
		clock_timestamp(), 'N', nm_usuario_w || ',', nr_sequencia_w, 'N',
		nr_seq_classif_w, clock_timestamp());

	Insert into COT_COMPRA_ENVIO(
		nr_sequencia, nr_cot_compra, dt_atualizacao, nm_usuario,
		dt_envio, ie_forma_envio, ds_observacao)
	values (
		nextval('cot_compra_envio_seq'), nr_cot_compra_w, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), 'D', WHEB_MENSAGEM_PCK.get_texto(298946));

	commit;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
