CREATE OR REPLACE PROCEDURE cot_liberar_item_desqualif ( nr_seq_item_p bigint, nr_documento_p bigint, ds_justificativa_p text, ie_funcao_liberar_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_funcao_liberar_p - Função que será realizada a liberação
C   - Cotação de Compra;
O   - Ordem Compra;
NF - Nota Fiscal;

nr_documento_p -
NR_SEQUENCIA 	= Sequência da Nota Fiscal;
NR_ORDEM_COMPRA 	= Número da ordem de compra;
*/
BEGIN

if (ie_funcao_liberar_p = 'C') then
	begin
	update	cot_compra_forn_item
	set	dt_liberacao 		= clock_timestamp(),
		nm_usuario_lib		= nm_usuario_p,
		ds_justificativa_lib		= ds_justificativa_p
	where	nr_sequencia		= nr_seq_item_p;
	end;
end if;

if (ie_funcao_liberar_p = 'O') and (coalesce(nr_documento_p,0) <> 0) then
	begin
	update	ordem_compra_item
	set	dt_lib_estrut_pj		= clock_timestamp(),
		nm_usuario_lib		= nm_usuario_p,
		ds_justificativa_lib		= ds_justificativa_p
	where	nr_ordem_compra		= nr_documento_p
	and	nr_item_oci		= nr_seq_item_p;
	end;
end if;

if (ie_funcao_liberar_p = 'NF') and (coalesce(nr_documento_p,0) <> 0) then
	begin
	update	nota_fiscal_item
	set	dt_lib_estrut_pj		= clock_timestamp(),
		nm_usuario_lib		= nm_usuario_p,
		ds_justificativa_lib		= ds_justificativa_p
	where	nr_sequencia		= nr_documento_p
	and	nr_item_nf		= nr_seq_item_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
