CREATE OR REPLACE PROCEDURE cpa_desf_reprov_pag_titulo ( nm_usuario_p text, nr_titulo_p bigint) AS $body$
BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	begin
	
	update	conta_pagar_lib
	set	dt_reprovacao  = NULL
	where	nr_titulo = nr_titulo_p
	and (
		nm_usuario_lib	= nm_usuario_p
		or (cd_perfil 	= obter_perfil_ativo)
	)
	and	coalesce(nr_bordero::text, '') = ''
	and	coalesce(nr_seq_banco_escrit::text, '') = '';
	
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
