SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpa_reprovar_pagamento_bordero ( nm_usuario_p text, nr_bordero_p bigint, nr_titulo_p bigint) AS $body$
DECLARE


		
ie_reprovacao_w		regra_lib_cp.ie_reprovacao%type;
		

BEGIN

select	coalesce(max(ie_reprovacao),'U')
into STRICT 	ie_reprovacao_w
from 	regra_lib_cp a
where 	a.cd_estabelecimento = obter_estabelecimento_ativo
and 	a.ie_origem = 'BP';

if (nr_titulo_p <> 0) then
	begin	
	if (ie_reprovacao_w = 'U') then
		update	conta_pagar_lib
		set		dt_reprovacao	= clock_timestamp()
		where	nr_bordero		= nr_bordero_p
			and		nr_titulo	= nr_titulo_p
			and (
                nm_usuario_lib	= nm_usuario_p
                or (cd_perfil = obter_perfil_ativo)
            )	
		;
	else
		update	conta_pagar_lib
		set		dt_reprovacao	= clock_timestamp()
		where	nr_bordero		= nr_bordero_p
		and		nr_titulo		= nr_titulo_p
		;	
	end if;
	end;
else	
	begin
	if (ie_reprovacao_w = 'U') then
		update	conta_pagar_lib
		set		dt_reprovacao	= clock_timestamp()
		where	
			nr_bordero		= nr_bordero_p
			and (
                nm_usuario_lib	= nm_usuario_p
                or (cd_perfil = obter_perfil_ativo)
            )	
			and		coalesce(nr_titulo::text, '') = '';
	else
		update	conta_pagar_lib
		set	dt_reprovacao	= clock_timestamp()
		where	nr_bordero	= nr_bordero_p
		and	coalesce(nr_titulo::text, '') = '';	
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
