SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_adjust_rp_sequence (nr_seq_order_unit_p bigint) AS $body$
DECLARE


counter_w bigint := 1;

c_sequence CURSOR FOR
SELECT  	nr_sequencia,
            nr_sequencia_rp
from    	cpoe_rp
where   	nr_seq_cpoe_order_unit = nr_seq_order_unit_p
order by 	nr_sequencia;

BEGIN

for r_sequence in c_sequence loop
    if ((r_sequence.nr_sequencia_rp - counter_w) > 0) then
        update cpoe_rp
        set nr_sequencia_rp = counter_w
        where nr_sequencia = r_sequence.nr_sequencia;
	end if;
	counter_w := counter_w + 1;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
