SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_ajustar_reg_grid_adep ( nr_prescricao_p bigint, nr_atendimento_p bigint, nr_seq_registro_p bigint, ie_item_reg_p text, ie_situacao_adep_p text,/*Se o item está suspenso, aprazado, ect....*/
 nm_usuario_p text, ie_tipo_dieta_p text ) AS $body$
BEGIN
 
  /*Dietas*/
 
if (ie_item_reg_p = 'D') then 
  begin 
   if (ie_tipo_dieta_p = 'E') or (ie_tipo_dieta_p = 'S') then 
     CALL cpoe_gerar_grid_enteral(nr_atendimento_p, nr_seq_registro_p, nm_usuario_p);
   elsif (ie_tipo_dieta_p = 'L') then 
     CALL cpoe_gerar_grid_leite_deriv(nr_atendimento_p, nr_seq_registro_p, nm_usuario_p);
   elsif (ie_tipo_dieta_p = 'O') then 
     CALL cpoe_gerar_grid_oral(nr_atendimento_p, nr_seq_registro_p, nm_usuario_p);
   end if;
  end;
  /*Gasoterapia*/
 
elsif (ie_item_reg_p = 'G') then 
  CALL cpoe_gerar_grid_gas(nr_atendimento_p, nr_seq_registro_p, nm_usuario_p);
  /*Medicamentos*/
 
elsif (ie_item_reg_p = 'M') then 
  CALL cpoe_gerar_grid_material(nr_atendimento_p, nr_seq_registro_p, nm_usuario_p);
  /*Procedimentos*/
 
elsif (ie_item_reg_p = 'P') then 
  CALL cpoe_gerar_grid_proc(nr_atendimento_p, nr_seq_registro_p, nm_usuario_p);
  /*Recomendacao*/
 
elsif (ie_item_reg_p = 'R') then 
  CALL cpoe_gerar_grid_rec(nr_atendimento_p, nr_seq_registro_p, nm_usuario_p);
  /*Soluções*/
 
elsif (ie_item_reg_p = 'S') then 
  CALL cpoe_gerar_grid_solucao(nr_atendimento_p, nr_seq_registro_p, nm_usuario_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
