SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_aprovar_favoritos ( itens_liberar_p text, nm_usuario_p text) AS $body$
DECLARE

materialType 	varchar(2);
  obj RECORD;

BEGIN
for obj in (
	SELECT
	trim(both regexp_substr(str, '[^,]+', 1)) tp,
		(case trim(both regexp_substr(str, '[^,]+', 1)) when 'MA' then
			trim(both regexp_substr(str, '[^,]+', 3))
		else 
			trim(both regexp_substr(str, '[^,]+', 2))
		end) cd
	from (WITH RECURSIVE cte AS (
SELECT trim(both regexp_substr(str, '[^;]+', 1, 1)) str
	from (select itens_liberar_p str )(regexp_substr(str , '[^;]+', 1, level) IS NOT NULL AND (regexp_substr(str , '[^;]+', 1, level))::text <> '')  UNION ALL
SELECT trim(both regexp_substr(str, '[^;]+', 1, (c.level+1))) str
	from (select itens_liberar_p str ) JOIN cte c ON ()

) SELECT * FROM cte;
))
loop
	if (obj.tp IS NOT NULL AND obj.tp::text <> '') then
    if (obj.tp = 'MA') then
      materialType := 'MA';
    elsif (obj.tp = 'M')then
      materialType := 'M';
    end if;
		case obj.tp
			when materialType then --Material
				update cpoe_fav_material
					set ie_pendente = 'N' 
				where nr_sequencia = obj.cd 
					and nm_usuario = nm_usuario_p
					and coalesce(ie_pendente, 'N') = 'S';
			when 'N' then --Nutricao
				update cpoe_fav_dieta
					set ie_pendente = 'N' 
				where nr_sequencia = obj.cd 
					and nm_usuario = nm_usuario_p
					and coalesce(ie_pendente, 'N') = 'S';
			when 'P' then --Procedimentos
				update cpoe_fav_procedimento
					set ie_pendente = 'N' 
				where nr_sequencia = obj.cd 
					and nm_usuario = nm_usuario_p
					and coalesce(ie_pendente, 'N') = 'S';
			when 'G' then --Gasoterapia
				update cpoe_fav_gasoterapia
					set ie_pendente = 'N' 
				where nr_sequencia = obj.cd 
					and nm_usuario = nm_usuario_p
					and coalesce(ie_pendente, 'N') = 'S';
			when 'R' then --Recomendacao
				update cpoe_fav_recomendacao
					set ie_pendente = 'N' 
				where nr_sequencia = obj.cd 
					and nm_usuario = nm_usuario_p
					and coalesce(ie_pendente, 'N') = 'S';
		end case;
	end if;
end loop;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
