CREATE OR REPLACE PROCEDURE cpoe_atualizar_cadastro_exame ( nm_usuario_p text ) AS $body$
DECLARE


	nr_seq_proc_interno_w	proc_interno.nr_sequencia%type;

	C01 CURSOR FOR
		SELECT	nr_sequencia,
			nr_seq_exame
		from	exame_lab_rotina
		where (nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
		AND coalesce(ie_situacao,'A') = 'A'
		order by 1;

	c01_w c01%rowtype;


BEGIN

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	max(nr_sequencia)
	into STRICT	nr_seq_proc_interno_w
	from	proc_interno
	where	nr_seq_exame_lab = c01_w.nr_seq_exame;

	if (coalesce(nr_seq_proc_interno_w,0) > 0) then

		update	exame_lab_rotina
		set nr_seq_exame_interno = nr_seq_proc_interno_w,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_sequencia = c01_w.nr_sequencia
		and nr_seq_exame = c01_w.nr_seq_exame;
	end if;

end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
