CREATE OR REPLACE PROCEDURE cpoe_atualizar_exame_protocolo (nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_interno_w	proc_interno.nr_sequencia%type;

	C01 CURSOR FOR
		SELECT	nr_sequencia,
				cd_protocolo,
				nr_seq_exame
		from	protocolo_medic_proc
		where coalesce(nr_seq_proc_interno::text, '') = ''
		and  (nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
		order by 1;

c01_w c01%rowtype;

	C02 CURSOR FOR
		SELECT	nr_sequencia,
				cd_protocolo,
				cd_procedimento
		from	protocolo_medic_proc
		where coalesce(nr_seq_proc_interno::text, '') = ''
		and (cd_procedimento IS NOT NULL AND cd_procedimento::text <> '')
		order by 1;

c02_w c02%rowtype;


BEGIN

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	max(nr_sequencia)
	into STRICT	nr_seq_proc_interno_w
	from	proc_interno
	where	nr_seq_exame_lab = c01_w.nr_seq_exame
	and coalesce(ie_situacao,'A') = 'A';

	if (coalesce(nr_seq_proc_interno_w,0) > 0) then

		update	protocolo_medic_proc
		set nr_seq_proc_interno = nr_seq_proc_interno_w,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_sequencia = c01_w.nr_sequencia
		and cd_protocolo = c01_w.cd_protocolo
		and nr_seq_exame = c01_w.nr_seq_exame;
	end if;

end loop;
close C01;

open C02;
loop
fetch C02 into
	c02_w;
EXIT WHEN NOT FOUND; /* apply on C02 */

	select	max(nr_sequencia)
	into STRICT	nr_seq_proc_interno_w
	from	proc_interno
	where	cd_procedimento = c02_w.cd_procedimento
	and coalesce(ie_situacao,'A') = 'A';

	if (coalesce(nr_seq_proc_interno_w,0) > 0) then

		update	protocolo_medic_proc
		set nr_seq_proc_interno = nr_seq_proc_interno_w,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_sequencia = c02_w.nr_sequencia
		and cd_protocolo = c02_w.cd_protocolo
		and cd_procedimento = c02_w.cd_procedimento;
	end if;
end loop;
close C02;

commit;

end;
$body$
