CREATE OR REPLACE PROCEDURE cpoe_atualizar_inf_adic ( nr_atendimento_p bigint, nr_seq_cpoe_p bigint, ie_tipo_item_p text, dt_liberacao_enf_p timestamp, dt_liberacao_farm_p timestamp, cd_perfil_farm_p cpoe_inf_adic.cd_perfil_farm%type default null, cd_perfil_enf_p cpoe_inf_adic.cd_perfil_enf%type default null, cd_funcao_ativa_farm_p cpoe_inf_adic.cd_funcao_ativa_farm%type default null, cd_funcao_ativa_enf_p cpoe_inf_adic.cd_funcao_ativa_enf%type default null, ie_commit_p text default 'N', nr_seq_order_unit_p bigint default null) AS $body$
DECLARE


nr_seq_inf_adic_w	cpoe_inf_adic.nr_sequencia%type;
ds_erro_w			log_cpoe.ds_log%type;
scriptQuery_w		varchar(4000);
can_insert_rec_w	varchar(1) := 'S';


BEGIN


	scriptQuery_w := 'select	max(nr_sequencia) ' ||
			 ' from	cpoe_inf_adic ';
			
	if (ie_tipo_item_p = 'M') then
		scriptQuery_w := scriptQuery_w || ' where nr_seq_mat_cpoe = :nr_seq_cpoe_p ';
	elsif (ie_tipo_item_p = 'N') then
		scriptQuery_w := scriptQuery_w || ' where nr_seq_diet_cpoe = :nr_seq_cpoe_p ';
	elsif (ie_tipo_item_p = 'R') then
		scriptQuery_w := scriptQuery_w || ' where nr_seq_rec_cpoe = :nr_seq_cpoe_p ';
	elsif (ie_tipo_item_p = 'G') then
		scriptQuery_w := scriptQuery_w || ' where nr_seq_gaso_cpoe = :nr_seq_cpoe_p ';
	elsif (ie_tipo_item_p = 'P') then
		scriptQuery_w := scriptQuery_w || ' where nr_seq_exam_cpoe = :nr_seq_cpoe_p ';
        elsif (ie_tipo_item_p = 'D') then
		scriptQuery_w := scriptQuery_w || ' where nr_seq_dial_cpoe = :nr_seq_cpoe_p ';
	elsif (ie_tipo_item_p = 'H') then
		scriptQuery_w := scriptQuery_w || ' where nr_seq_hemo_cpoe = :nr_seq_cpoe_p ';
	elsif (ie_tipo_item_p = 'A') then
		scriptQuery_w := scriptQuery_w || ' where nr_seq_anat_cpoe = :nr_seq_cpoe_p ';
	else
		scriptQuery_w := scriptQuery_w || ' 1 = 2 ';
	end if;

	nr_seq_inf_adic_w := obter_valor_dinamico_bv(scriptQuery_w, 'nr_seq_cpoe_p='||nr_seq_cpoe_p||';', nr_seq_inf_adic_w);

	if (coalesce(nr_seq_inf_adic_w, 0) = 0) then
		
		if (coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'ja_JP') then
			if (nr_seq_order_unit_p IS NOT NULL AND nr_seq_order_unit_p::text <> '') then
				select	coalesce(max('S'),'N')
				into STRICT 	can_insert_rec_w
				from	cpoe_tipo_pedido
				where	ie_situacao = 'A'
				and 	cd_order_confirmation 	in ('1','2')
				and 	nr_sequencia 	= 	(	SELECT 	max(nr_seq_cpoe_tipo_pedido)
									from 	cpoe_order_unit
									where 	nr_sequencia = nr_seq_order_unit_p
								);
			end if;
		end if;

		if (can_insert_rec_w = 'S') then
			select	nextval('cpoe_inf_adic_seq')
			into STRICT	nr_seq_inf_adic_w
			;

			insert into cpoe_inf_adic(
				nr_sequencia,
				nr_atendimento,
				nm_usuario_nrec,
				nm_usuario,
				dt_atualizacao_nrec,
				dt_atualizacao,
				nr_seq_mat_cpoe,
				nr_seq_diet_cpoe,
				nr_seq_rec_cpoe,
				nr_seq_gaso_cpoe,
				nr_seq_exam_cpoe,
				nr_seq_dial_cpoe,
				nr_seq_hemo_cpoe,
				nr_seq_anat_cpoe)
			values (nr_seq_inf_adic_w,
				nr_atendimento_p,
				obter_usuario_ativo,
				obter_usuario_ativo,
				clock_timestamp(),
				clock_timestamp(),
				CASE WHEN ie_tipo_item_p='M' THEN  nr_seq_cpoe_p  ELSE null END ,
				CASE WHEN ie_tipo_item_p='N' THEN  nr_seq_cpoe_p  ELSE null END ,
				CASE WHEN ie_tipo_item_p='R' THEN  nr_seq_cpoe_p  ELSE null END ,
				CASE WHEN ie_tipo_item_p='G' THEN  nr_seq_cpoe_p  ELSE null END ,
				CASE WHEN ie_tipo_item_p='P' THEN  nr_seq_cpoe_p  ELSE null END ,
				CASE WHEN ie_tipo_item_p='D' THEN  nr_seq_cpoe_p  ELSE null END ,
				CASE WHEN ie_tipo_item_p='H' THEN  nr_seq_cpoe_p  ELSE null END ,
				CASE WHEN ie_tipo_item_p='A' THEN  nr_seq_cpoe_p  ELSE null END );
		end if;

	end if;

	if (coalesce(nr_seq_inf_adic_w, 0) > 0) then
		update	cpoe_inf_adic
		set	cd_funcao_ativa_enf = coalesce(cd_funcao_ativa_enf, CASE WHEN dt_liberacao_enf_p = NULL THEN  null  ELSE coalesce(cd_funcao_ativa_enf_p, obter_funcao_ativa) END ),
			cd_perfil_enf = coalesce(cd_perfil_enf, CASE WHEN dt_liberacao_enf_p = NULL THEN  null  ELSE coalesce(cd_perfil_enf_p, obter_perfil_ativo) END ),
			cd_funcao_ativa_farm = coalesce(cd_funcao_ativa_farm, CASE WHEN dt_liberacao_farm_p = NULL THEN  null  ELSE coalesce(cd_funcao_ativa_farm_p, obter_funcao_ativa) END ),
			cd_perfil_farm = coalesce(cd_perfil_farm, CASE WHEN dt_liberacao_farm_p = NULL THEN  null  ELSE coalesce(cd_perfil_farm_p, obter_perfil_ativo) END ),
			nm_usuario = obter_usuario_ativo,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_inf_adic_w;
	end if;
	
	if (coalesce(ie_commit_p, 'N') = 'S') then commit; end if;
	
exception
    when others then
    ds_erro_w  := substr('CPOE_ATUALIZAR_INF_ADIC - Erro: ' || sqlerrm(SQLSTATE),1,4000) || ' nr_seq_cpoe_p: ' || nr_seq_cpoe_p || ' ie_tipo_item_p: ' || ie_tipo_item_p;
    CALL gravar_log_cpoe(ds_erro_w, nr_atendimento_p);
end;
$body$
