CREATE OR REPLACE PROCEDURE cpoe_atualizar_periodo_vig_ant (ds_horarios_p INOUT cpoe_material.ds_horarios%type, hr_prim_horario_p INOUT cpoe_material.hr_prim_horario%type, dt_incio_p INOUT timestamp) AS $body$
DECLARE


ds_das_w 			varchar(255) := obter_texto_tasy(439834, wheb_usuario_pck.get_nr_seq_idioma);
ds_horarios_w		cpoe_material.ds_horarios%type;
hr_prim_horario_w	cpoe_material.hr_prim_horario%type;
dt_prim_horario_w	timestamp;


BEGIN
if (ds_horarios_p IS NOT NULL AND ds_horarios_p::text <> '') then
	if (position(ds_das_w in ds_horarios_p) > 0) then
		ds_horarios_w		:= replace(cpoe_reordenar_horarios(clock_timestamp(), cpoe_retirar_das_as(ds_horarios_p)),'  ',' ');
	else
		ds_horarios_w		:= replace(cpoe_reordenar_horarios(clock_timestamp(), ds_horarios_p),'  ',' ');
	end if;

	hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);
	dt_prim_horario_w	:= to_date(to_char(clock_timestamp(),'dd/mm/yyyy ') || hr_prim_horario_w, 'dd/mm/yyyy hh24:mi');

	ds_horarios_w := replace(padroniza_horario_prescr(ds_horarios_w, null),'A','');

	ds_horarios_p 		:= ds_horarios_w;
	hr_prim_horario_p 	:= hr_prim_horario_w;
	dt_incio_p			:= dt_prim_horario_w;

	if (dt_prim_horario_w < clock_timestamp()) then
		dt_incio_p := dt_incio_p + 1;
	end if;

end if;

end;
