SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_atualizar_trev_elem_mat (nr_seq_ele_cpoe_p bigint, nm_usuario_p text, ie_processo_hidrico_p text) AS $body$
DECLARE

					
nr_seq_elem_mat_he_w		bigint;
nr_seq_he_elem_mat_w		bigint;
qt_conversao_ml_w			double precision;
qt_maior_w					double precision := 0;

c01 CURSOR FOR
SELECT	b.nr_sequencia,
		a.qt_conversao_ml
from 	nut_elem_material a,
		cpoe_trev_elem_mat b
where	a.nr_sequencia = b.nr_seq_elem_mat
and		b.nr_seq_ele_cpoe = nr_seq_ele_cpoe_p
and		a.ie_situacao	= 'A'
and		a.ie_padrao	= 'S'
and		coalesce(a.ie_tipo,'NPT') = 'NPT';

BEGIN

if (ie_processo_hidrico_p = 'S') then

	for c01_w in C01
	loop
		nr_seq_elem_mat_he_w := c01_w.nr_sequencia;
		qt_conversao_ml_w := c01_w.qt_conversao_ml;

		if (qt_maior_w < qt_conversao_ml_w) then
			qt_maior_w 	:= qt_conversao_ml_w;
			nr_seq_he_elem_mat_w := nr_seq_elem_mat_he_w;
		end if;
	end loop;
	
	delete from cpoe_trev_elem_mat
	where  nr_sequencia = nr_seq_he_elem_mat_w;
	commit;
	
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
