CREATE OR REPLACE PROCEDURE cpoe_cancel_schedule_by_exam ( nr_seq_agenda_p agenda_paciente.nr_sequencia%type, nr_seq_lista_espera_p agenda_lista_espera.nr_sequencia%type, nr_seq_agenda_int_p agenda_integrada.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cd_agenda_w		agenda_paciente.cd_agenda%type;


BEGIN

	if (coalesce(nr_seq_agenda_p, 0) > 0) then
		select	coalesce(max(cd_agenda),0)
		into STRICT	cd_agenda_w
		from	agenda_paciente
		where	nr_sequencia = nr_seq_agenda_p
		and	ie_status_agenda = 'N';

		if (cd_agenda_w > 0) then
			CALL alterar_status_agenda(cd_agenda_w, nr_seq_agenda_p, 'C', null, null, 'N', nm_usuario_p);
		end if;

	elsif (coalesce(nr_seq_lista_espera_p, 0) > 0) then		
		update agenda_lista_espera
		set ie_status_espera = 'C'
		where	nr_sequencia	= nr_seq_lista_espera_p;

	elsif (coalesce(nr_seq_agenda_int_p,0) > 0) then
		delete	from ageint_horarios_usuario ahu
		where ahu.nr_seq_ageint_lib in (SELECT	alu.nr_sequencia
					from	ageint_lib_usuario alu
					where	alu.nr_seq_ageint = nr_seq_agenda_int_p);

		delete	from ageint_lib_usuario
		where	nr_seq_ageint = nr_seq_agenda_int_p;
		
		delete from ageint_marcacao_usuario
		where	nr_seq_ageint = nr_seq_agenda_int_p;

		delete	from agenda_integrada_item
		where	nr_seq_agenda_int = nr_seq_agenda_int_p;

		delete  from ageint_arq_anexo_email
		where	nr_seq_agenda_int = nr_seq_agenda_int_p;
		
		delete	from agenda_integrada
		where	nr_sequencia = nr_seq_agenda_int_p;
	end if;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
