CREATE OR REPLACE PROCEDURE cpoe_consist_regra_prescr_mat ( nr_atendimento_p bigint, cd_material_p bigint, ie_assoc_proc_p text, ie_via_aplicacao_p text, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint, cd_convenio_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_paciente_p text, dt_inicio_p timestamp, ds_erro_p INOUT text, ds_mensagem_p INOUT text, ie_abortar_p INOUT text, cd_intervalo_p cpoe_material.cd_intervalo%type default null, ie_duracao_p cpoe_material.ie_duracao%type default null) AS $body$
DECLARE

	ds_retorno_w   varchar(4000) := 'S';

BEGIN

select 	coalesce(max(ie_forma_consistencia),'X')
into STRICT 	ie_abortar_p
from 	regra_consiste_prescr_par
where 	nr_seq_regra   = 168
and	(( cd_perfil      = cd_perfil_p ) or ( coalesce(cd_perfil::text, '') = '' ) );

if ( ie_abortar_p <> 'X' ) then
	ds_retorno_w := cpoe_obter_se_medic_lib_med(	nr_atendimento_p,
							cd_material_p,
							ie_assoc_proc_p,
							ie_via_aplicacao_p,
							ie_tipo_atendimento_p ,
							cd_setor_atendimento_p,
							cd_perfil_p,
							cd_convenio_p,
							nm_usuario_p,
							cd_estabelecimento_p,
							cd_paciente_p,
							dt_inicio_p,
							cd_intervalo_p,
							ie_duracao_p);
end if;

if ( ds_retorno_w = 'S' ) then
	ds_erro_p := 'N';
elsif ( ds_retorno_w = 'M' ) then
	ds_erro_p := 'M';
elsif ( ds_retorno_w = 'D' ) then
	ds_erro_p := 'D';
else
	ds_erro_p := 'S';
	if ( ds_retorno_w <> 'N' ) then
		ds_mensagem_p := ds_retorno_w;
	else
		ds_mensagem_p := obter_desc_expressao(554794,'');
	end if;
end if;

end;
$body$
