CREATE OR REPLACE PROCEDURE cpoe_consis_med_insert ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



C01 CURSOR FOR
 SELECT *
 from cpoe_material
 where nr_sequencia = nr_sequencia_p;

ds_erro_w varchar(4000);
ds_mensagem_w varchar(4000);
ie_abortar_w varchar(30);


BEGIN

if ( pkg_i18n.get_user_locale()='en_AU') then
Begin
for r_c01 in c01 loop
 begin

 SELECT * FROM cpoe_consist_regra_prescr_mat(r_c01.nr_atendimento, r_c01.cd_material, 'N', r_c01.ie_via_aplicacao, Obter_Tipo_Atendimento(r_c01.nr_atendimento), obter_setor_atendimento(r_c01.nr_atendimento), obter_perfil_ativo, Obter_Convenio_Atendimento(r_c01.nr_atendimento), wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, r_c01.cd_pessoa_fisica, r_c01.dt_inicio, ds_erro_w, ds_mensagem_w, ie_abortar_w, r_c01.cd_intervalo, r_c01.ie_duracao) INTO STRICT ds_erro_w, ds_mensagem_w, ie_abortar_w;


 if (ds_erro_w = 'M') then
  update cpoe_material
  set cd_medico  = NULL,
   IE_ORDERED_ON_BEHALF = 'S'
  where nr_sequencia =  nr_sequencia_p;
 end if;

 end;
end loop;
end;
end if;


end;
$body$
