CREATE OR REPLACE PROCEDURE cpoe_consis_proced_insert ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



C01 CURSOR FOR
 SELECT *
 from cpoe_procedimento
 where nr_sequencia = nr_sequencia_p;

ds_erro_w varchar(4000);
ds_mensagem_w varchar(4000);
ie_abortar_w varchar(30);


BEGIN

if ( pkg_i18n.get_user_locale()='en_AU') then
Begin
for r_c01 in c01 loop
 begin

 SELECT * FROM cpoe_consist_regra_presc_proce(r_c01.nr_atendimento, null, null, null, r_c01.nr_seq_proc_interno, obter_perfil_ativo, obter_setor_atendimento(r_c01.nr_atendimento), r_c01.cd_material_exame, nm_usuario_p, r_c01.dt_prev_execucao, ds_erro_w, ds_mensagem_w, ie_abortar_w) INTO STRICT ds_erro_w, ds_mensagem_w, ie_abortar_w;


 if (ds_erro_w = 'M') then
  update cpoe_procedimento
  set cd_medico  = NULL,
   ie_ordered_on_behalf = 'S',
   ie_exige_ciencia = 'S'
  where nr_sequencia =  nr_sequencia_p;
 end if;

 end;
end loop;
end;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
