SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_copy_proc_interno_doenca (nr_seq_proc_cpoe_p bigint, nr_seq_order_unit_p bigint) AS $body$
DECLARE


	nm_usuario_w				proc_interno_doenca.nm_usuario%type;
	cd_doenca_cid_w				proc_interno_doenca.cd_doenca_cid%type;
	nr_seq_proc_cpoe_w			proc_interno_doenca.nr_seq_proc_cpoe%type;
	nr_seq_proc_interno_w		cpoe_procedimento.nr_sequencia%type;
	ie_nome_doenca_w			proc_interno_classif.ie_nome_doenca%type;
	
	c01 CURSOR FOR
		SELECT	nm_usuario,
				cd_doenca_cid
		from	proc_interno_doenca
		where	nr_seq_order_unit = nr_seq_order_unit_p;


BEGIN

	select	max(nr_seq_proc_interno)
	into STRICT	nr_seq_proc_interno_w
	from	cpoe_procedimento
	where	nr_sequencia = nr_seq_proc_cpoe_p;
	
	select	max(tmp1.ie_nome_doenca)
	into STRICT	ie_nome_doenca_w
	from (SELECT pic.ie_nome_doenca,
					pic.qt_max_nome_doenca,
					0 as prioridade
			from	proc_interno_classif pic
			inner join	proc_interno pi on pic.nr_sequencia = pi.nr_seq_classif
			where	pic.ie_situacao = 'A'
			and		pi.ie_situacao = 'A'
			and		pi.nr_sequencia = nr_seq_proc_interno_w
			
union

			SELECT 'N' AS ie_nome_doenca,
					0 as qt_max_nome_doenca,
					1 as prioridade
			
			order by prioridade) tmp1 LIMIT 1;
	
	if (ie_nome_doenca_w = 'S')then

		open c01;
		loop
		fetch c01 into
			nm_usuario_w,
			cd_doenca_cid_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

			insert into proc_interno_doenca(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						cd_doenca_cid,
						nr_seq_proc_cpoe,
						nr_seq_order_unit)
			values (
						nextval('proc_interno_doenca_seq'),
						clock_timestamp(),
						nm_usuario_w,
						clock_timestamp(),
						nm_usuario_w,
						cd_doenca_cid_w,
						nr_seq_proc_cpoe_p,
						null);
			
			end;
		end loop;
		close c01;
		
		commit;
		
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
