CREATE OR REPLACE PROCEDURE cpoe_copy_protocols_npt ( nr_seq_npt_cpoe_p bigint, nm_usuario_p text, nr_seq_npt_cpoe_new_p bigint) AS $body$
DECLARE



C01 CURSOR FOR
	SELECT	*
	FROM CPOE_NPT_PRODUTO
	WHERE	nr_seq_npt_cpoe = nr_seq_npt_cpoe_p;

c01_w c01%rowtype;

C02 CURSOR FOR
	SELECT *
	FROM CPOE_NPT_ELEMENTO
	WHERE	nr_seq_npt_cpoe = nr_seq_npt_cpoe_p;

c02_w c02%rowtype;


BEGIN
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

			insert into CPOE_NPT_PRODUTO(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_elem_mat,
						nr_seq_elemento,
						nr_seq_npt_cpoe,
						cd_material,
						qt_volume,
						qt_vol_1_fase,
						qt_vol_2_fase,
						qt_vol_3_fase,
						qt_vol_4_fase,
						qt_vol_cor,
						qt_protocolo,
						qt_dose,
						cd_unidade_medida,
						ie_prod_adicional,
						ie_somar_volume,
						ie_supera_limite_uso,
						ie_item_valido,
						ie_modificado)
			values (			nextval('cpoe_npt_produto_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						c01_w.nr_seq_elem_mat,
						c01_w.nr_seq_elemento,
						nr_seq_npt_cpoe_new_p,
						c01_w.cd_material,
						c01_w.qt_volume,
						c01_w.qt_vol_1_fase,
						c01_w.qt_vol_2_fase,
						c01_w.qt_vol_3_fase,
						c01_w.qt_vol_4_fase,
						c01_w.qt_vol_cor,
						c01_w.qt_protocolo,
						c01_w.qt_dose,
						c01_w.cd_unidade_medida,
						c01_w.ie_prod_adicional,
						c01_w.ie_somar_volume,
						c01_w.ie_supera_limite_uso,
						c01_w.ie_item_valido,
						c01_w.ie_modificado);

		end;
	end loop;
	close C01;

	open C02;
	loop
	fetch C02 into
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

			insert into CPOE_NPT_ELEMENTO(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_npt_cpoe,
						cd_unidade_medida,
						nr_seq_elemento,
						qt_diaria,
						qt_kcal,
						qt_protocolo,
						qt_elem_kg_dia,
						ie_prod_adicional,
						pr_total,
						pr_concentracao,
						ie_prim_fase,
						ie_seg_fase,
						ie_terc_fase,
						ie_quar_fase,
						ie_npt,
						qt_osmolaridade,
						ie_unid_med,
						qt_volume_final,
						ie_editado,
						qt_grama_nitrogenio,
						ie_tipo_elemento,
						ie_glutamina,
						ie_item_valido
						)
			values (			nextval('cpoe_npt_elemento_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_npt_cpoe_new_p,
						c02_w.cd_unidade_medida,
						c02_w.nr_seq_elemento,
