						c02_w.qt_elem_kg_dia,
						c02_w.ie_prod_adicional,
						c02_w.pr_total,
						c02_w.pr_concentracao,
						c02_w.ie_prim_fase,
						c02_w.ie_seg_fase,
						c02_w.ie_terc_fase,
						c02_w.ie_quar_fase,
						c02_w.ie_npt,
						c02_w.qt_osmolaridade,
						c02_w.ie_unid_med,
						c02_w.qt_volume_final,
						c02_w.ie_editado,
						c02_w.qt_grama_nitrogenio,
						c02_w.ie_tipo_elemento,
						c02_w.ie_glutamina,
						c02_w.ie_item_valido);

		end;
	end loop;
	close C02;

