CREATE OR REPLACE PROCEDURE cpoe_delete_hispostal_peca ( nr_sequencia_p bigint, ie_tabela_p text) AS $body$
BEGIN
	if (ie_tabela_p = 'CPOE_PRESCR_PROC_PECA' or ie_tabela_p = '')then
		DELETE FROM CPOE_PRESCR_PROC_PECA
		WHERE nr_seq_cpoe_proc = nr_sequencia_p;
	end if;

	if (ie_tabela_p = 'CPOE_PRESCR_HISTOPATOL' or ie_tabela_p = '')then
		DELETE FROM CPOE_PRESCR_HISTOPATOL
		WHERE nr_seq_proc_anatomia = nr_sequencia_p;
	end if;

	if (ie_tabela_p = 'CPOE_PRESCR_CITOPATOLOGICO' or ie_tabela_p = '')then
		DELETE FROM CPOE_PRESCR_CITOPATOLOGICO
		WHERE nr_seq_proc_anatomia = nr_sequencia_p;
	end if;

	if (ie_tabela_p = '')then
		DELETE FROM CPOE_ANATOMIA_PATOLOGICA
		WHERE nr_sequencia = nr_sequencia_p;
	end if;

	commit;


end;
$body$
