CREATE OR REPLACE PROCEDURE cpoe_delete_itens_trev (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT	nr_sequencia
from	cpoe_material
where	nr_atendimento = nr_atendimento_p
and	coalesce(dt_liberacao::text, '') = ''
and	ie_trev = 'S';

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	for c01_w in C01
	loop
		CALL cpoe_remover_item('M', c01_w.nr_sequencia, nm_usuario_p);
	end loop;
	commit;
end if;

end;
