CREATE OR REPLACE PROCEDURE cpoe_delete_older_justify ( nr_seq_cpoe_p cpoe_justif_interacao.nr_seq_cpoe_princ%type, ds_list_sequences_p text, ie_tipo_item_p text default null) AS $body$
BEGIN

	if (coalesce(nr_seq_cpoe_p, 0) > 0) then

		delete from cpoe_justif_interacao b
		 where b.nr_sequencia in (
								SELECT a.nr_sequencia
								  from cpoe_justif_interacao a
								  left join table(lista_pck.obter_lista(coalesce(ds_list_sequences_p, ''))) x on x.nr_registro = a.nr_sequencia
								 where ((nr_seq_cpoe_princ = nr_seq_cpoe_p and coalesce(ie_tipo_item_p, 'M') = 'M')
								    or (nr_seq_proc_princ = nr_seq_cpoe_p and coalesce(ie_tipo_item_p, 'M') = 'P'))
								   and coalesce(x.nr_registro::text, '') = ''
								);

		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
