CREATE OR REPLACE PROCEDURE cpoe_excluir_regist_invalidos ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
 
	delete	FROM cpoe_recomendacao 
	where	nr_atendimento = nr_atendimento_p 
	and		nm_usuario	= nm_usuario_p 
	and		coalesce(dt_liberacao::text, '') = '' 
	and		coalesce(ie_item_valido,'S') = 'N';
	 
	delete	FROM cpoe_material 
	where	nr_atendimento = nr_atendimento_p 
	and		nm_usuario	= nm_usuario_p 
	and		coalesce(dt_liberacao::text, '') = '' 
	and		coalesce(ie_item_valido,'S') = 'N';
	 
	 
	delete	FROM cpoe_procedimento 
	where	nr_atendimento = nr_atendimento_p 
	and		nm_usuario	= nm_usuario_p 
	and		coalesce(dt_liberacao::text, '') = '' 
	and		ie_item_valido = 'S';
	 
	delete	FROM cpoe_dieta 
	where	nr_atendimento = nr_atendimento_p 
	and		nm_usuario	= nm_usuario_p 
	and		coalesce(dt_liberacao::text, '') = '' 
	and		coalesce(ie_item_valido,'S') = 'N';
 
	delete	FROM cpoe_dialise 
	where	nr_atendimento = nr_atendimento_p 
	and		nm_usuario	= nm_usuario_p 
	and		coalesce(dt_liberacao::text, '') = '' 
	and		coalesce(ie_item_valido,'S') = 'N';
	 
	delete	FROM cpoe_hemoterapia 
	where	nr_atendimento = nr_atendimento_p 
	and		nm_usuario	= nm_usuario_p 
	and		coalesce(dt_liberacao::text, '') = '' 
	and		coalesce(ie_item_valido,'S') = 'N';
	 
	delete	FROM cpoe_gasoterapia 
	where	nr_atendimento = nr_atendimento_p 
	and		nm_usuario	= nm_usuario_p 
	and		coalesce(dt_liberacao::text, '') = '' 
	and		coalesce(ie_item_valido,'S') = 'N';
	 
	commit;
 
end if;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
